import './App.css';
import {useState} from "react";

function App() {
    // Isso é o conjunto da variavel, o que chamamos de estados.. variavel nome eo metodo setName para popular a variavel.
    const [nome, setName] = useState(); // Hooks

    // Isso é uma função
    const populaNome = (event) => {
        setName(event.target.value);
    }

    return (
        <div>
            <nav className="navbar bg-light">
                <div className="container">
                    <a className="navbar-brand" href="#">
                    </a>
                </div>
            </nav>

            <div className="col-sm-4 card">
                <div className="input-group flex-nowrap">
                    <span className="input-group-text" id="addon-wrapping">Nome: {nome}</span>
                    <input type="text" className="form-control" placeholder="Username" onChange={populaNome}/>
                </div>
            </div>

            <button className="btn btn-success">Clique Aqui</button>
        </div>
    );
}

export default App;
