import './App.css';
import {useState} from "react";
import 'antd/dist/antd.css';
import { Layout } from 'antd';
import { LoadingOutlined, SmileOutlined, SolutionOutlined, UserOutlined } from '@ant-design/icons';
import { Steps } from 'antd';
import React from 'react';

function App() {
    // Isso é o conjunto da variavel, o que chamamos de estados.. variavel nome eo metodo setName para popular a variavel.
    const [nome, setName] = useState(); // Hooks

    // Isso é uma função
    const populaNome = (event) => {
        setName(event.target.value);
    }

    const App: React.FC = () => {

    }

    const { Header, Footer, Sider, Content } = Layout;

    const { Step } = Steps;


    return (
        <div>            
            <Layout>
                <Header style={{color:'white'}}>Header</Header>
                <Steps>
    <Step status="finish" title="Login" icon={<UserOutlined />} />
    <Step status="finish" title="Verification" icon={<SolutionOutlined />} />
    <Step status="process" title="Pay" icon={<LoadingOutlined />} />
    <Step status="wait" title="Done" icon={<SmileOutlined />} />
  </Steps>
                <Layout>
                    <Sider style={{color:'white'}}>Sider</Sider>
                    <Content>Content</Content>
                </Layout>
                <Footer>Footer</Footer>
            </Layout>
        </div>
    );
}

export default App;
