                        <Step status="process" title="ITEM 1" icon={<SolutionOutlined />} />
                        <Step status="process" title="ITEM 1" icon={<SolutionOutlined />} />
                    </Steps>
                </section>
                <Layout>
                    <Sider style={{color:'white'}}>Sider</Sider>
                    <Content>
                        <p>Nome</p>
                        <Input placeholder="Basic usage" />;
                        <p>CPF</p>
                        <Input placeholder="Basic usage" />
                        <p>RG</p>
                        <Input placeholder="Basic usage" />
                    </Content>
                    <Content>
                        <p>Sexo</p><input></input>
                        <p>Data de Nascimento</p>
                        <p>Cargo</p>
                    </Content>
                </Layout>
                <Footer>Footer</Footer>
            </Layout>
        </div>
    );
}

export default App;
